import React, { useEffect } from 'react'
import ReactPlayer from 'react-player'
import { Route } from 'react-router';

function Stream(props) {
    const mySiteStream=[
        {name:'Gotaga',link:'https://www.twitch.tv/gotaga'},
        {name:'Solary',link:'https://www.twitch.tv/solary'},
        {name:'Skyyart',link:'https://www.twitch.tv/skyyart'}
    ]

    function getAllSteam(props)
    {
        const tab=[];
        mySiteStream.map((item,index)=>
        {
            <div style={{ backgroundColor: 'red', marginRight: "2%", marginTop: '1%' }}>
            Stream {mySiteStream[props.index].name}
            <ReactPlayer width='380px' height='300px' url='https://www.twitch.tv/gotaga' />
        </div>
        })
        return tab;
    }
    return (
        getAllSteam(props)   
    );
}

export default Stream;
