import React, { Component } from 'react';
import {
    Link
} from "react-router-dom";
import Menu from '../Componant/Menu';
import News from '../Componant/MainNews';
import setup from '../Img/setup.jpg'

function MediumActualite() {

    const articles = [];
    for (let i = 4; i < 7; i++) {
        articles.push(
            <>
                <div style={{ marginLeft: '10%', marginTop: '1%', marginBottom: '1%', justifyContent: 'center', alignItems: 'center', display: 'flex' }}>
                    <img style={{ width: '20%', borderRadius: 10 }} src={setup} alt="article" />
                    <News id={i} description={false} />
                </div>
                <hr style={{ backgroundColor: 'grey', marginLeft: '10%', marginRight: '10%', height: "0.05px", marginBottom: '1%' }} />
            </>
        )

    }
    return (
        <div>
            {articles}
            <div style={{justifyContent:'center', alignItems: 'center'}}>
                <button style={{ borderColor: '#440BD4', backgroundColor: '#440BD4', borderWidth: 2, borderStyle: 'solid', borderRadius: 15, padding: 5, alignContent: 'center', color: 'white', fontWeight: 'bold', textAlign: 'center' }}>Voir plus</button>
            </div>
        </div>
    );
}

export default MediumActualite;
