import axios from 'axios';
import cheerio from 'cheerio';
import React, { Component } from 'react';

class ScrapSteam extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    }
  }
  async componentDidMount() {
    const html = await axios.get('https://www.leagueofgraphs.com/fr/rankings/summoners');
    // console.log(html.data)
    const lexport = await cheerio.load(html.data);
    let ledata=[];
    lexport('.box box-padding-10').each((i,elem)=>
    {
      if(i<=3)
      {
          ledata.push({
          title: lexport(elem).find('td.text-right hide-for-small-down').text(),
        })
      }
    })
    this.setState({data:ledata})
    console.log(ledata)
  }

  render() {
    return (
      <div >
        {console.log(this.data)}
      </div>
    );
  }
}
export default ScrapSteam;
