import React, { Component } from 'react';
import {
    Link
} from "react-router-dom";
import Menu from '../Componant/Menu';
import News from '../Componant/MainNews';
import setup from '../Img/setup.jpg'

function MediumActualite() {

    const articles = [];
    for (let i = 1; i < 4; i++) {
        articles.push(
        <div style={{marginLeft:'10%',marginRight:'50%',marginTop:'5%',marginBottom:'5%',flexWrap:'wrap',height:20,flex:1}}>
            <img style={{width:'50%' ,borderRadius:10}} src={setup} alt="article" />
            <News id={i} />
        </div>)

    }
    return (
        <div>
            {articles}
            <hr style={{backgroundColor: 'grey',marginLeft:'10%',marginRight:'10%',height:"0.05px"}}/>
        </div>
    );
}

export default MediumActualite;
