import React, { Component } from 'react';
import '../Style/News.css';
import * as rssParser from 'react-native-rss-parser';

class News extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title : '',
            content: [],
        }


    }
    componentDidMount() {
        fetch('http://www.nasa.gov/rss/dyn/breaking_news.rss')
            .then((response) => response.text())
            .then((responseData) => rssParser.parse(responseData))
            .then((rss) => {
                // console.log(rss.title);
                // console.log(rss.items);
                this.setState({ title: rss.title , content:rss.items })
                
            });
    }

    render() {
        return (
            <div> 
                {this.state.content[0].description}
                {this.state.content.map((e, i) =>e.title)}
                {this.state.content.map((e, i) =>e.description)}
            </div>
        );
    }
}

export default News;
