import {
    Link
} from "react-router-dom";
import ScrapSteam from "../Componant/Scraper";
import Menu from '../Componant/Menu';
import { RadialChart } from 'react-vis';
import { useState } from "react";
import { IoIosSearch } from "react-icons/io";


function Stats() {

    const myData = [{ angle: 1 ,label:'kill'}, { angle: 5,label:'death' }, { angle: 2 ,label:'assist'}]
    const [keyword, setKeyword] = useState('')
    return (
        <div>
            <Menu />
            <div style={{display:'flex',flexDirection:'row',justifyContent:'center',marginTop:20}}>
            <IoIosSearch size='50'/>
            <input
                style={{ width: '30%',fontWeight:'bold',borderRadius:5 }}
                value={keyword}
                placeholder={"Entre ton pseudo"}
                onChange={(e) => setKeyword(e.target.value)}
            />
            </div>
            <div style={{display:'flex',flexDirection:'row',justifyContent:'center',marginTop:20,marginLeft:'5%',marginRight:'5%'}}>
                <div style={{display:'flex',flex:3,borderRadius:1,borderColor:'grey',borderWidth:1,border:'solid',marginRight:'5%'}}>
                <RadialChart
                data={myData}   
                showLabels={true}
                colorRange={['#E92EFB', '#FF2079', '#440BD4', '#04005E', 'black']}
                width={300}
                height={300}
            />
                </div>
                <div style={{flex:1,alignItems:'center' ,borderRadius:1,borderColor:'grey',borderWidth:1,border:'solid',marginLeft:'5%'}}>
                    <h1>Stats général</h1>
                    <div>Les 10 premiers joueurs LOL
                    <ScrapSteam/>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Stats;
