import React, { Component } from 'react';
import {
    Link
} from "react-router-dom";
import Menu from '../Componant/Menu';
import News from '../Componant/MainNews';
import setup from '../Img/setup.jpg'

function MediumActualite() {

    const articles = [];
    for (let i = 1; i < 4; i++) {
        articles.push(
            <>
                <div style={{ marginLeft: '10%', marginTop: '1%', marginBottom: '2%', justifyContent: 'center', alignItems: 'center', display: 'flex' }}>
                    <img style={{ width: '20%', borderRadius: 10 }} src={setup} alt="article" />
                    <News id={i} description={false} />
                </div>
                <hr style={{ backgroundColor: 'grey', marginLeft: '10%', marginRight: '10%', height: "0.05px" }} />
            </>
        )

    }
    return (
        <div>
            {articles}
        </div>
    );
}

export default MediumActualite;
