import React from 'react'
import menu from '../Component/Menu';
import footer from '../Img/footer.png'
import Equipebox from '../Component/Equipebox'
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

import {
    Link
} from "react-router-dom";


function Recruitment() {

    function fulldisplay() {
        const tab = []
        for (let index = 0; index < 12; index++) {
            tab.push(<Equipebox />)
        }
        return tab;
    }

    return (
        <div >
            <menu />
            <div style={{ display: 'flex', flexDirection: 'row' }}>
                <div style={{ flex: 1, boxShadow: "2px 0px 0px #9E9E9E" }}>
                    <div style={{ margin: 10 }}>
                        Jeu:
                       <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                            Open Menu
                        </Button>
                        <Menu
                            id="simple-menu"
                            anchorEl={anchorEl}
                            keepMounted
                            open={Boolean(anchorEl)}
                            onClose={handleClose}
                        >
                            <MenuItem onClick={handleClose}>Profile</MenuItem>
                            <MenuItem onClick={handleClose}>My account</MenuItem>
                            <MenuItem onClick={handleClose}>Logout</MenuItem>
                        </Menu>
                    </div>
                </div>
                <div style={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap', justifyContent: 'center', flex: 3, backgroundColor: '#e3e5e5' }}>
                    {fulldisplay()}
                </div>
            </div>
            <img style={{ width: '100%' }} src={footer} alt="footer" />
        </div>
    );
}

export default Recruitment;
