import axios from 'axios';
import cheerio from 'cheerio';
import React, { Component } from 'react';

class ScrapSteam extends Component {
  constructor(props) {
    super(props);
    this.state = {

    }
  }

  render() {
    const html =  axios.get('https://www.realtor.com/news/real-estate-news/');
    const $ =  cheerio.load(html.data);
    let data = [];

    return (
      <div >
        {$('.site-main article').each((i, elem) => {
          if (i <= 3) {
            data.push({
              image: $(elem).find('img.wp-post-image').attr('src'),
              title: $(elem).find('h2.entry-title').text(),
              excerpt: $(elem).find('p.hide_xxs').text().trim(),
              link: $(elem).find('h2.entry-title a').attr('href')
            })
          }
        })}
        {console.log(data)}
      </div>
    );
  }
}
export default ScrapSteam;
