import React, { useEffect } from 'react'
import ReactPlayer from 'react-player'
import { Route } from 'react-router';

function Stream() {
    const mySiteStream=[
        {name:'Gotaga',link:'https://www.twitch.tv/gotaga'},
        {name:'Solary',link:'https://www.twitch.tv/solary'},
        {name:'Skyyart',link:'https://www.twitch.tv/skyyart'}
    ]

    function getAllSteam()
    {
        const tab=[];
        mySiteStream.map((item,index)=>
        {
        <div style={{ backgroundColor: 'red', marginRight: "2%", marginTop: '1%' }}>
            Stream {item.name}
            <ReactPlayer width='380px' height='300px' url={item.link} />
         </div>
        })
        return tab;
    }
    return (
        getAllSteam()   
    );
}

export default Stream;
