import React, { useEffect } from 'react'
import ReactPlayer from 'react-player'
import { Route } from 'react-router';

function Stream() {
    const mySiteStream = [
        { name: 'Gotaga', link: 'https://www.twitch.tv/gotaga' },
        { name: 'Solary', link: 'https://www.twitch.tv/solary' },
        { name: 'Skyyart', link: 'https://www.twitch.tv/skyyart' }
    ]

    function getAllSteam() {
        const tab = [];
        mySiteStream.map((item, index) => {
            tab.push(
                <div style={{ backgroundColor: '#440BD4', marginRight: "2%", marginTop: '1%',textAlign:'center',borderRadius:5 }}>
                    Stream {item.name}
                    <ReactPlayer
                        width='380px'
                        height='300px'
                        url={item.link} 
                    />
                </div>
            )
        })
        return tab;
    }
    return (
        getAllSteam()
    );
}

export default Stream;
